import React from 'react';
import QuizContainer from './QuizContainer';

const QuestionCard = ({ question, onAnswerSelected }) => {
    if (!question) {
        return <div>Question non trouv√©e.</div>;
    }
    return (
        <div className='flex flex-col text-center w-full'>
            <img src={question.image} />
            
            <div className='flex flex-col p-6'>
                <h2>{question.question}</h2>

                <div className='flex flex-row flex-wrap mt-4 text-lg align-center'>
                    <div className='row'>
                        <button className='p-4' onClick={() => onAnswerSelected('A')}>{question.choixA}</button>
                        <button className='p-4' onClick={() => onAnswerSelected('B')}>{question.choixB}</button>
                    </div>
                    <div className='row'>
                        <button className='p-4' onClick={() => onAnswerSelected('C')}>{question.choixC}</button>
                        <button className='p-4' onClick={() => onAnswerSelected('D')}>{question.choixD}</button>
                    </div>

                </div>
            </div>




        </div>
    );
}

export default QuestionCard;
