import React, { useState, useEffect } from 'react';
import QuestionCard from './QuestionCard';
import CustomProgressBar from './CustomProgressBar';
import ScoreDisplay from './ScoreDisplay';
import questionsData from './json/quiz.json';

const QuizContainer = () => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [score, setScore] = useState(0);
    const [startTime, setStartTime] = useState(null);
    const [quizFinished, setQuizFinished] = useState(false);


    useEffect(() => {
        // Mélangez aléatoirement les questions et prenez les 20 premières
        const shuffledQuestions = questionsData.sort(() => 0.5 - Math.random()).slice(0, 20);
        setQuestions(shuffledQuestions);
        setCurrentQuestionIndex(0);
        setScore(0);
        setStartTime(Date.now());
    }, []);
    

    const handleAnswer = (answer) => {
        if (questions[currentQuestionIndex].correct === answer) {
            // Mets à jour le score si la réponse est correcte
            setScore(score + 1);
        }
    
        // Passez à la question suivante
        const nextQuestionIndex = currentQuestionIndex + 1;
        if (nextQuestionIndex < questions.length) {
            setCurrentQuestionIndex(nextQuestionIndex);
        } else {
            setQuizFinished(true); // Marquer le quiz comme terminé
        }
    };

    
    return (
        <div>
        {!quizFinished ? (
            <>
                <CustomProgressBar current={currentQuestionIndex + 1} total={questions.length} />
                <br />
                {/* Afficher le niveau de la question actuelle */}
                <div>
                    Niveau: {questions[currentQuestionIndex] && questions[currentQuestionIndex].niveau}
                </div>
                <br />

                <QuestionCard
                    question={questions[currentQuestionIndex]}
                    onAnswerSelected={handleAnswer}
                />
            </>
        ) : (
            <ScoreDisplay score={score} totalQuestions={questions.length} />
        )}
    </div>
    );
}

export default QuizContainer;
