import React from "react";

const CustomProgressBar = ({ current, total }) => {
    const percentage = (current / total) * 100;

    return (
        <>
            <div className="w-full">
                <div className="progressBar h-2 text-xs font-medium text-center " style={{ width: `${percentage}%`}}/>
            </div>         
            <p className='absolute left-0 top-4 ml-2 text-sm'>Question {current} / {total}</p>
        </>
    );
};

export default CustomProgressBar;
