import React from 'react';

const QuestionCard = ({ question, onAnswerSelected, niveau }) => {
    if (!question) {
        return <div>Question non trouv√©e.</div>;
    }
    return (
        <>
        <div className='text-xs'>{niveau}</div>
        <div className='flex flex-col text-center bg-[rgba(255,255,255,0.1)] m-10 rounded-lg'>
            <div className='w-full h-[400px] overflow-hidden mx-auto rounded-tl-lg rounded-tr-lg opacity-80'>
                <img className='w-full h-full object-cover' src={question.image} alt="Question" />
            </div>
            
            <div className='flex flex-col p-6 items-center'>
                <h2 className='pb-10'>{question.question}</h2>

                <div className='grid grid-cols-1 md:grid-cols-2 gap-6 text-lg pb-10'>
                    <button className='px-2 button' onClick={() => onAnswerSelected('A')}>{question.choixA}</button>
                    <button className='px-2 button' onClick={() => onAnswerSelected('B')}>{question.choixB}</button>
                    <button className='px-2 button' onClick={() => onAnswerSelected('C')}>{question.choixC}</button>
                    <button className='px-2 button' onClick={() => onAnswerSelected('D')}>{question.choixD}</button>
                </div>
            </div>
        </div>
        </>
    );
}

export default QuestionCard;
