import React from "react";

const CustomProgressBarGold = ({ current, total }) => {
    const percentage = (current / total) * 100;

    return (
        <>
            <div className="w-full">
                <div className="progressBarGold h-2 text-xs font-medium text-center z-10 rounded-full shadow-4xl" style={{ width: `${percentage}%`}}/>
            </div>         
            <p className='absolute left-0 top-5 ml-2 text-md z-10'>Question {current} / {total}</p>
        </>
    );
};

export default CustomProgressBarGold;
