import React from 'react';
import QuizContainer from './QuizContainer';


const QuestionCard = ({ question, onAnswerSelected }) => {
    if (!question) {
        return <div>Question non trouvée.</div>;
    }
    return (
        <div>
            <h2>{question.question}</h2>
            {/* Afficher les choix de réponse ici */}
            {/* Par exemple: */}
            <button onClick={() => onAnswerSelected('A')}><span>A</span> {question.choixA}</button>
            <button onClick={() => onAnswerSelected('B')}><span>B</span> {question.choixB}</button>
            <button onClick={() => onAnswerSelected('C')}><span>C</span> {question.choixC}</button>
            <button onClick={() => onAnswerSelected('D')}><span>D</span> {question.choixD}</button>
            {/* Répétez pour les choix B, C, D */}
        </div>
    );
}

export default QuestionCard;
