import ProgressBar from 'react-progressbar';


const CustomProgressBar = ({ current, total }) => {
    const percentage = (current / total) * 100;
    const text = percentage +"%"

    return (
        <>



            <div className='mx-10 mt-10'>
            <div className=' left-[50px] top-(-4px)'>
                <p className='text-sm'>Question {current} / {total}</p>
            </div>
            <div className="w-full">
                <div className="bg-[#d30a84] h-4 text-xs font-medium text-blue-100 text-center p-0.5 leading-none rounded-tl-lg rounded-tr-lg" style={{ width: {percentage}}}/>
            </div>
            </div>            
        </>
    );
};

export default CustomProgressBar;
