import React from 'react';
import CustomProgressBar from './CustomProgressBar';

const QuestionCard = ({ question, onAnswerSelected, niveau }) => {
    if (!question) {
        return <div>Question non trouvée.</div>;
    }

    // Fonction pour déterminer la classe en fonction du niveau
    const getNiveauClass = (niveau) => {
        switch (niveau) {
            case 'FACILE':
                return 'absolute z-10 top-2 right-2 bg-green-100 text-green-800 text-xs font-medium px-2.5 py-0.5 rounded-full';
            case 'INTERMEDIAIRE':
                return 'absolute z-10 top-2 right-2 bg-yellow-100 text-yellow-800 text-xs font-medium px-2.5 py-0.5 rounded-full';
            case 'DIFFICILE':
                return 'absolute z-10 top-2 right-2 bg-red-100 text-red-800 text-xs font-medium px-2.5 py-0.5 rounded-full';
            default:
                return ''; // Ou une classe par défaut si nécessaire
        }
    };

    const niveauClass = getNiveauClass(niveau);
    return (
        <>
        <div className='relative flex flex-col text-center bg-[#1b1f38] z-0 h-[90vh]'>
        <CustomProgressBar 
                        current={currentQuestionIndex + 1} 
                        total={questions.length} 
                        className='rounded-full'
                />
            <div className={niveauClass}>{niveau}</div>
            <div className='w-full z-0 h-[40vh] overflow-hidden mx-auto opacity-80 mb-4'>
                <img className='w-full h-full object-cover' src={question.image} alt="Question" />
            </div>
            
            <div className='flex flex-col p-6 items-center align-middle'>
                <h2 className='pb-10'>{question.question}</h2>

                <div className='grid grid-cols-1 md:grid-cols-2 gap-6 text-lg pb-10'>
                    <button className='px-2 button' onClick={() => onAnswerSelected('A')}><span class="btn-text">{question.choixA}</span></button>
                    <button className='px-2 button' onClick={() => onAnswerSelected('D')}><span class="btn-text">{question.choixD}</span></button>
                    <button className='px-2 button' onClick={() => onAnswerSelected('C')}><span class="btn-text">{question.choixC}</span></button>
                    <button className='px-2 button' onClick={() => onAnswerSelected('B')}><span class="btn-text">{question.choixB}</span></button>
                </div>
            </div>
        </div>
        </>
    );
}

export default QuestionCard;
