import React from "react";

const CustomProgressBar = ({ current, total }) => {
    const percentage = (current / total) * 100;
    const text = percentage +"%"

    return (
        <>
            <div className='mx-10 mt-10'>
                <p className='ml-2 text-sm'>Question {current} / {total}</p>
                <div className="w-full">
                    <div className="bg-Purple h-4 text-xs font-medium text-center p-0.5 leading-none rounded-tl-lg rounded-tr-lg" style={{ width: {percentage}}}/>
                </div>
            </div>            
        </>
    );
};

export default CustomProgressBar;
