import React from 'react';

const QuestionCard = ({ question, onAnswerSelected, niveau }) => {
    if (!question) {
        return <div>Question non trouvée.</div>;
    }

    // Fonction pour déterminer la classe en fonction du niveau
    const getNiveauClass = (niveau) => {
        switch (niveau) {
            case 'FACILE':
                return 'absolute z-10 top-[-6px] right-0 bg-green-100 text-green-800 text-xs font-medium px-2.5 py-0.5 rounded-full';
            case 'INTERMEDIAIRE':
                return 'absolute z-10 top-[-6px] right-0 bg-yellow-100 text-yellow-800 text-xs font-medium px-2.5 py-0.5 rounded-full';
            case 'DIFFICILE':
                return 'absolute z-10 top-[-6px] right-0 bg-indigo-100 text-indigo-800 text-xs font-medium px-2.5 py-0.5 rounded-full';
            default:
                return ''; // Ou une classe par défaut si nécessaire
        }
    };
    return (
        <>
        <div className='relative flex flex-col text-center bg-[rgba(255,255,255,0.1)] m-10 rounded-lg'>
            <div className={niveauClass}>{niveau}</div>
            <div className='w-full z-0 h-[400px] overflow-hidden mx-auto rounded-tl-lg rounded-tr-lg opacity-80'>
                <img className='w-full h-full object-cover' src={question.image} alt="Question" />
            </div>
            
            <div className='flex flex-col p-6 items-center'>
                <h2 className='pb-10'>{question.question}</h2>

                <div className='grid grid-cols-1 md:grid-cols-2 gap-6 text-lg pb-10'>
                    <button className='px-2 button' onClick={() => onAnswerSelected('A')}>{question.choixA}</button>
                    <button className='px-2 button' onClick={() => onAnswerSelected('B')}>{question.choixB}</button>
                    <button className='px-2 button' onClick={() => onAnswerSelected('C')}>{question.choixC}</button>
                    <button className='px-2 button' onClick={() => onAnswerSelected('D')}>{question.choixD}</button>
                </div>
            </div>
        </div>
        </>
    );
}

export default QuestionCard;
