import React from 'react';
import QuizContainer from './QuizContainer';

const QuestionCard = ({ question, onAnswerSelected }) => {
    if (!question) {
        return <div>Question non trouv√©e.</div>;
    }
    return (
        <div className='flex flex-col text-center p-10 bg-[rgba(255,255,255,0.1)]'>
            <img src={question.image} />
            
            <div className='flex flex-col p-6'>
                <h2>{question.question}</h2>

                <div className='flex flex-col mt-4 text-lg'>
                    <button className='py-2 text-white bg-gradient-to-br from-[#6f4c7c] to-[#53335b] hover:bg-[#c9a7c3]  focus:outline-none rounded-lg' onClick={() => onAnswerSelected('A')}>
                        {question.choixA}
                    </button>
                    <button className='py-2' onClick={() => onAnswerSelected('B')}>{question.choixB}</button>
                    <button className='py-2' onClick={() => onAnswerSelected('C')}>{question.choixC}</button>
                    <button className='py-2' onClick={() => onAnswerSelected('D')}>{question.choixD}</button>
                </div>
            </div>
        </div>
    );
}

export default QuestionCard;
