import React, { useState, useEffect } from 'react';
import CustomProgressBar from './CustomProgressBar';
import questionsData from './json/quiz.json';

const QuestionCard = ({ question, onAnswerSelected, niveau }) => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [startTime, setStartTime] = useState(null);

    useEffect(() => {
        // Mélangez aléatoirement les questions et prenez les 20 premières
        const shuffledQuestions = questionsData.sort(() => 0.5 - Math.random()).slice(0, 20);
        setQuestions(shuffledQuestions);
        setCurrentQuestionIndex(0);
        setScore(0);
        setStartTime(Date.now());
    }, []);
    

    const handleAnswer = (answer) => {
        if (questions[currentQuestionIndex].correct === answer) {
