import React from 'react';
import question from './json/quiz.json';

const QuestionCard = ({ question, onAnswerSelected }) => {
    return (
        <div>
            <h2>{question.question}</h2>
            {/* Afficher les choix de réponse ici */}
            {/* Par exemple: */}
            <button onClick={() => onAnswerSelected('A')}>{question.choixA}</button>
            <button onClick={() => onAnswerSelected('B')}>{question.choixB}</button>
            <button onClick={() => onAnswerSelected('C')}>{question.choixC}</button>
            <button onClick={() => onAnswerSelected('D')}>{question.choixD}</button>
            {/* Répétez pour les choix B, C, D */}
        </div>
    );
}

export default QuestionCard;
