import React, { useState, useEffect } from 'react';
import CustomProgressBar from './CustomProgressBar';

const QuestionCard = ({ question, onAnswerSelected, niveau, currentQuestionIndex, totalQuestions }) => {
    const [selectedChoice, setSelectedChoice] = useState('');

    // Réinitialiser le choix sélectionné chaque fois que la question change
    useEffect(() => {
        setSelectedChoice('');
    }, [question]);

    const handleAnswerSelection = (choice) => {
        setSelectedChoice(choice);
        onAnswerSelected(choice);
    };

    if (!question) {
        return <div>Question non trouvée.</div>;
    }

    // Fonction pour déterminer la classe en fonction du niveau
    const getNiveauClass = (niveau) => {
        switch (niveau) {
            case 'FACILE':
                return 'absolute z-10 top-4 right-2 bg-green-100 text-green-800 text-xs font-medium px-2.5 py-1 rounded-full';
            case 'INTERMEDIAIRE':
                return 'absolute z-10 top-4 right-2 bg-yellow-100 text-yellow-800 text-xs font-medium px-2.5 py-1 rounded-full';
            case 'DIFFICILE':
                return 'absolute z-10 top-4 right-2 bg-red-100 text-red-800 text-xs font-medium px-2.5 py-1 rounded-full';
            default:
                return ''; // Ou une classe par défaut si nécessaire
        }
    };

    const niveauClass = getNiveauClass(niveau);

    return (
        <>
        <div className='relative flex flex-col text-center bg-[rgba(255,255,255,0.02)] z-0 md:h-[90vh]'>
            <CustomProgressBar current={currentQuestionIndex} total={totalQuestions} />
            <div className={niveauClass}>{niveau}</div>
            <div className='w-full z-0 h-[40vh] mx-auto mb-4 shadow-inner'>
                <img className='w-full h-full object-cover' src={question.image} alt="Question" />
            </div>
            
            <div className='flex flex-col mt-10 items-center shadow-t-xl'>
                <h2 className='pb-10 px-10 text-3xl'>{question.question}</h2>

                <div className='grid grid-cols-1 md:grid-cols-2 gap-10 text-2xl mb-10'>
                {['A', 'B', 'C', 'D'].map((choice) => (
                        <button
                            key={choice}
                            className={`px-2 button ${selectedChoice === choice ? 'selected-class' : ''}`}
                            onClick={() => handleAnswerSelection(choice)}
                        >
                            <span class="btn-text">{question[`choix${choice}`]}</span>
                        </button>
                    ))}
                </div>
                <img src='./images/signature.png' className='absolute bottom-0 right-0' width={"10%"} />
            </div>
        </div>
        </>
    );
}

export default QuestionCard;
