import React from "react";

const CustomProgressBarGold = ({ current, total, timeLeft, totalTime  }) => {
    const percentage = (timeLeft / totalTime) * 100;

    return (
        <>
            <div className="w-full">
                <div className="progressBarGold h-2 text-xs font-medium text-center z-10 rounded-full shadow-4xl" style={{ width: `${percentage}%`}}/>
            </div>
            <p className='absolute left-0 top-6 ml-2 text-md z-10 px-3 py-1 bg-[rgba(0,0,0,0.2)] rounded-full tewt-white'>Question {current} / {total}</p>
            <p className='absolute right-0 top-[-4vh] ml-2 text-md z-10 px-3 py-1 bg-[rgba(0,0,0,0.2)] rounded-full text-4xl tewt-white'>Temps restant {timeLeft}</p>
        </>
    );
};

export default CustomProgressBarGold;
